import React from "react"
import { Card } from 'antd';

type MessageProps = {
  message: string
  userName: string
}

export const Message = (props: MessageProps) => {
  return (
    <>
      <p><span style={{ fontWeight: "bold" }}>{props.userName}: </span>
        {props.message}</p>

      <Card style={{ width: 300 }}>
        <p>Card content</p>
        <p>Card content</p>
        <p>Card content</p>
      </Card>,
    </>
  )
}
