import React from "react"
import { Card } from 'antd';

type MessageProps = {
  message: string
  userName: string
}

export const Message = (props: MessageProps) => {
  return (
    <>
      <Card
        style={{ backgroundColor: "turquoise", width: 300, borderRadius: 30, color: "whitesmoke", fontSize: 16, marginBottom: 10 }}>
        <span>
          <span style={{ fontWeight: "bold" }}>{props.userName}: </span>
          {props.message}
        </span>
      </Card>



      <p style={{
        borderRadius: '25px',
        padding: '4px 15px 4px'
      }}>
        {props.message}
      </p>

    </>
  )
}
