import { FC, useState, useEffect } from 'react';
import 'antd/dist/antd.css';
import "./styles.css"
import { Button, Input, Layout, Breadcrumb, Form } from 'antd';
import { SendOutlined } from '@ant-design/icons';
import { Message } from "./Message"
import { db } from "./firebase"

const { Header, Content, Footer } = Layout;

export const App: FC = () => {
  // to types.d.ts
  type Messages = {
    text: string
    userName: string
  }[]

  const [input, setInput] = useState("")
  const [messages, setMessages] = useState<Messages>([])
  const [userName, setUserName] = useState("unknown")

  // useEffect(() => {
  //   db.collection("messages").onSnapshot(snap => {
  //     setMessages(snap.docs.map((doc) =>{
  //       doc.data()
  //     }))
  //   })
  // },[])

  useEffect(() => {


    db.collection("messages").onSnapshot((snap) => {
      snap.docs.forEach((doc) => {
        console.log(doc.data())
      })
    })




  })




  useEffect(() => {
    const userNamePrompt = prompt("Please enter your name")
    if (userNamePrompt) {
      setUserName(userNamePrompt)
    }

  }, [])


  const sendMessage = () => {
    // evt.preventDefault()
    setMessages([...messages, { text: input, userName: userName }])
    setInput("")
  }

  return (
    <div >
      <Layout className="layout">
        <Header className="site-page-header">
          <div className="logo" />
          <h1>Messenger</h1>
        </Header>
        <Content style={{ padding: '0 50px' }}>
          <Breadcrumb style={{ margin: '16px 0' }}>
            {/* <Breadcrumb.Item>Home</Breadcrumb.Item>
            <Breadcrumb.Item>List</Breadcrumb.Item>
            <Breadcrumb.Item>App</Breadcrumb.Item> */}
          </Breadcrumb>
          <h2>Hello, {userName}</h2>
          <div className="site-layout-content">
            {messages.map((message) => {
              return (
                // <p key={Math.random()}>{msg}</p>
                <Message key={Math.random()} loggedInUserName={userName} messageObj={message} />
              )
            })}
            <Form.Provider onFormFinish={sendMessage}>
              <Form >
                <div>
                  <Input
                    value={input}
                    onChange={evt => setInput(evt.target.value)}
                    style={{ width: 200 }}
                  />
                  <Button
                    disabled={!input}
                    onClick={sendMessage}
                    // shape="round"
                    type="primary"
                    icon={<SendOutlined />}>Send
                </Button>
                </div>
              </Form>
            </Form.Provider>
          </div>
        </Content>
        <Footer style={{ textAlign: 'center' }}>2021</Footer>
      </Layout>

    </div>
  );
}


