import React from "react"
import { Card } from 'antd';

type MessageProps = {
  message: string
  userName: string
}

export const Message = (props: MessageProps) => {
  return (
    <>
      <div style={{ backgroundColor: "#1778F2" }}>
        <span
          style={{ borderRadius: 30, color: "whitesmoke", fontSize: 16, marginBottom: 10 }}>
          <p>
            <span style={{ fontWeight: "bold" }}>{props.userName}: </span>
            {props.message}
          </p>
        </span>
      </div>

    </>
  )
}
