import { FC, useState } from 'react';
import './App.css';
import 'antd/dist/antd.css';
import { Button } from 'antd';
import { DownloadOutlined } from '@ant-design/icons';



export const App: FC = () => {

  type Messages = [] | string[]

  const [input, setInput] = useState("")
  const [messages, setMessages] = useState<Messages>([])

  console.log(messages)

  const sendMessage = (evt: React.MouseEvent | React.KeyboardEvent) => {
    evt.preventDefault()
    setMessages([...messages, input])
    setInput("")
  }


  return (
    <div className="App">
      <h1>Hello to the Chat</h1>

      {messages.map((msg: string) => {
        return (
          <p key={Math.random()}>{msg}</p>
        )
      })}


      <form onSubmit={(e: React.SyntheticEvent) => sendMessage}>
        <input value={input} onChange={evt => setInput(evt.target.value)} />
        {/* <button type="submit" >Send</button> */}
        <Button onClick={sendMessage} shape="round" type="primary" icon={<DownloadOutlined />}>Send Message</Button>
      </form>
    </div>
  );
}
