import { FC, useState } from 'react';
import './App.css';
import 'antd/dist/antd.css';
import { Button, Input, Layout, Menu, Breadcrumb } from 'antd';
import { SendOutlined } from '@ant-design/icons';

const { Header, Content, Footer } = Layout;

export const App: FC = () => {

  type Messages = [] | string[]

  const [input, setInput] = useState("")
  const [messages, setMessages] = useState<Messages>([])

  console.log(messages)

  const sendMessage = (evt: React.MouseEvent | React.KeyboardEvent) => {
    evt.preventDefault()
    setMessages([...messages, input])
    setInput("")
  }


  return (
    <div className="App">




      <Layout className="layout">
        <Header>
          <div className="logo" />
          <Menu theme="dark" mode="horizontal" defaultSelectedKeys={['2']}>
            <Menu.Item key="1">nav 1</Menu.Item>
            <Menu.Item key="2">nav 2</Menu.Item>
            <Menu.Item key="3">nav 3</Menu.Item>
          </Menu>
        </Header>
        <Content style={{ padding: '0 50px' }}>
          <Breadcrumb style={{ margin: '16px 0' }}>
            <Breadcrumb.Item>Home</Breadcrumb.Item>
            <Breadcrumb.Item>List</Breadcrumb.Item>
            <Breadcrumb.Item>App</Breadcrumb.Item>
          </Breadcrumb>
          <div className="site-layout-content">


            <h1>Hello to the Chat</h1>

            {messages.map((msg: string) => {
              return (
                <p key={Math.random()}>{msg}</p>
              )
            })}


            <form onSubmit={(e: React.SyntheticEvent) => sendMessage}>
              <div>
                <Input
                  value={input}
                  onChange={evt => setInput(evt.target.value)}
                  style={{ width: 200 }}
                />
                <Button
                  disabled={!input}
                  onClick={sendMessage}
                  shape="round"
                  type="primary"
                  icon={<SendOutlined />}>Send Message
        </Button>
              </div>

            </form>


          </div>
        </Content>
        <Footer style={{ textAlign: 'center' }}>Ant Design Â©2018 Created by Ant UED</Footer>
      </Layout>








    </div>
  );
}
