import React from "react"
import { Card } from 'antd';

type MessageProps = {
  message: string
  userName: string
}

export const Message = (props: MessageProps) => {
  return (
    <>
      <Card
        style={{ backgroundColor: "turquoise", borderRadius: 30, color: "whitesmoke", fontSize: 16, marginBottom: 10 }}>
        <span>
          <span style={{ fontWeight: "bold" }}>{props.userName}: </span>
          {props.message}
        </span>
      </Card>

    </>
  )
}
